import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";import{o,c as d,d as c}from"./app.db8a6c79.js";const a={},i=c('<h1 id="jwt" tabindex="-1"><a class="header-anchor" href="#jwt" aria-hidden="true">#</a> JWT</h1><p>\u968F\u7740\u79FB\u52A8\u4E92\u8054\u7F51\u7684\u5174\u8D77\uFF0C\u4F20\u7EDF\u57FA\u4E8E <code>Session+Cookie</code> \u7684Web\u7F51\u7AD9\u8BA4\u8BC1\u65B9\u5F0F\u8F6C\u53D8\u6210\u4E86\u57FA\u4E8E <code>OAuth2</code> \u7B49\u5F00\u653E\u6388\u6743\u534F\u8BAE\u7684\u5355\u70B9\u767B\u5F55\u6A21\u5F0F\uFF08SSO\uFF0CSingle Sign On\uFF09\uFF0C\u76F8\u5E94\u7684\u57FA\u4E8E\u670D\u52A1\u5668 <code>Session+Cookie</code> \u7684 <code>Auth</code> \u624B\u6BB5\u4E5F\u8F6C\u53D8\u6210 <code>JWT(Json Web Token)</code> \u4E3A\u4EE3\u8868<code>Token Auth</code>\u673A\u5236\u3002</p><p><code>JWT</code>\u662F\u4E00\u4E2A\u975E\u5E38\u8F7B\u5DE7\u7684\u89C4\u8303\uFF0C\u8FD9\u4E2A\u89C4\u8303\u5141\u8BB8\u6211\u4EEC\u4F7F\u7528 <code>JWT</code> \u5728\u4E24\u4E2A\u7EC4\u7EC7\u4E4B\u95F4\u4F20\u9012\u5B89\u5168\u53EF\u9760\u7684\u4FE1\u606F\u3002<code>Session+Cookie</code>\u7684\u65B9\u5F0F\u628A\u8BA4\u8BC1\u4FE1\u606F\u653E\u5728\u4E86\u670D\u52A1\u7AEF\uFF0C<code>JWT</code>\u628A\u8BA4\u8BC1\u4FE1\u606F\u653E\u5728\u4E86\u5BA2\u6237\u7AEF\uFF0C\u51CF\u8F7B\u4E86\u670D\u52A1\u7AEF\u7684\u5185\u5B58\u538B\u529B</p><ul><li><code>JWS</code>\uFF1A\u7B7E\u540D\u8FC7\u7684 <code>JWT</code></li><li><code>JWE</code>\uFF1A\u90E8\u5206 Payload \u7ECF\u8FC7\u52A0\u5BC6\u7684 <code>JWT</code></li><li><code>JWA</code>\uFF1A <code>JWT</code> \u7528\u5230\u7684\u5BC6\u7801\u5B66\u7B97\u6CD5</li><li><code>JWK</code>\uFF1A<code>JWT</code> \u7684\u5BC6\u94A5</li></ul><h2 id="\u7279\u70B9" tabindex="-1"><a class="header-anchor" href="#\u7279\u70B9" aria-hidden="true">#</a> \u7279\u70B9</h2><ul><li><code>JWT</code> \u662F\u65E0\u72B6\u6001\u7684</li><li><code>JWT</code> \u4E0D\u4EC5\u53EF\u4EE5\u7528\u4E8E\u8BA4\u8BC1\uFF0C\u4E5F\u53EF\u4EE5\u7528\u4E8E\u4EA4\u6362\u4FE1\u606F\uFF0C\u56E0\u4E3A Payload \u90E8\u5206\u53EF\u4EE5\u81EA\u5B9A\u4E49\u5185\u5BB9</li><li><code>JWT</code> \u53EF\u4EE5\u9632\u6B62\u7BE1\u6539\uFF0C\u56E0\u4E3A <code>JWT</code> \u5305\u542B\u4E86\u7B7E\u540D</li><li><code>JWT</code> \u4E0D\u80FD\u5B58\u653E\u654F\u611F\u6570\u636E\uFF0C\u56E0\u4E3A Payload \u90E8\u5206\u53EA\u8FDB\u884C\u4E86base64\u7F16\u7801</li><li><code>JWT</code> \u662F\u4E00\u6B21\u6027\u7684\uFF0C\u5982\u679C\u60F3\u4FEE\u6539\u5185\u5BB9\uFF0C\u5FC5\u987B\u91CD\u65B0\u9881\u53D1\u4E00\u4E2A</li></ul><p><code>JWT(Json Web Token)</code> \u6709\u4E24\u79CD\u5B9E\u73B0\u65B9\u5F0F\uFF1A<code>JWS(Json Web Signature)</code> \u4E0E <code>JWE(Json Web Encryption)</code></p><p><img src="https://wingbun-notes-image.oss-cn-guangzhou.aliyuncs.com/images/20210227182347.png" alt=""></p><h2 id="jws" tabindex="-1"><a class="header-anchor" href="#jws" aria-hidden="true">#</a> JWS</h2><p><code>JWS(JSON Web Signature)</code>\uFF1A<code>Signed JWT</code><strong>\u7B7E\u540D\u8FC7\u7684<code>JWT</code></strong>\uFF0C\u662F\u4E00\u4E2A<strong>\u7EDF\u4E00\u8868\u8FBE\u5F62\u5F0F\u7684\u5B57\u7B26\u4E32</strong></p><p><img src="https://wingbun-notes-image.oss-cn-guangzhou.aliyuncs.com/images/20210227182556.png" alt=""></p><h3 id="header-\u5934\u90E8" tabindex="-1"><a class="header-anchor" href="#header-\u5934\u90E8" aria-hidden="true">#</a> Header \u5934\u90E8</h3><p>\u5934\u90E8\u7528\u4E8E\u63CF\u8FF0\u5173\u4E8E\u8BE5 <code>JWT</code> \u7684\u6700\u57FA\u672C\u7684\u4FE1\u606F\uFF0C\u4F8B\u5982\u5176\u7C7B\u578B\u4EE5\u53CA\u7B7E\u540D\u6240\u7528\u7684\u7B97\u6CD5\u7B49</p><ul><li>alg\uFF1A\u7B7E\u540D\u6240\u7528\u7684\u7B97\u6CD5\uFF0C\u5982 HMAC \u6216 RSA \u7B49</li><li>typ\uFF1A\u4EE4\u724C\u7684\u7C7B\u578B</li><li>jti(\u53EF\u9009)\uFF1A\u3010<code>JWT</code> ID\u3011\u4EE3\u8868\u6B63\u5728\u4F7F\u7528 <code>JWT</code> \u7684\u7F16\u53F7\uFF0C\u8FD9\u4E2A\u7F16\u53F7\u5BF9\u5E94\u670D\u52A1\u7AEF\u5E94\u5F53\u552F\u4E00\uFF0C\u4E5F\u53EF\u4EE5\u653E\u5728 Payload \u4E2D</li><li>cty(\u53EF\u9009)\uFF1A\u3010Content Type\u3011\u8FD9\u4E2A\u6BD4\u8F83\u5C11\u89C1\u3002\u5F53 Payload \u4E2D\u4E3A\u4EFB\u610F\u6570\u636E\u65F6\uFF0C\u8FD9\u4E2A\u503C\u65E0\u9700\u8BBE\u7F6E\uFF1B\u4F46\u662F\u5F53 Payload \u4E2D\u4E5F\u6709 <code>JWT</code> \u7684\u65F6\u5019\uFF0C\u4E5F\u5C31\u662F<strong>\u5D4C\u5957</strong> <code>JWT</code> \u65F6\uFF0C\u8FD9\u4E2A\u503C\u5FC5\u987B\u8BBE\u5B9A\u4E3A <code>JWT</code></li></ul><p>JSON \u5185\u5BB9\u8981\u7ECF Base64 \u7F16\u7801\u751F\u6210\u5B57\u7B26\u4E32\u6210\u4E3A Header</p><h3 id="payload-\u8D1F\u8F7D" tabindex="-1"><a class="header-anchor" href="#payload-\u8D1F\u8F7D" aria-hidden="true">#</a> Payload \u8D1F\u8F7D</h3><ul><li>iss\uFF1A\u3010issuer\u3011\u53D1\u5E03\u8005\u7684 url</li><li>sub\uFF1A\u3010subject\u3011\u8BE5 <code>JWT</code> \u9762\u5411\u7684\u7528\u6237</li><li>aud\uFF1A\u3010audience\u3011\u63A5\u6536\u8005\u7684 url</li><li>exp\uFF1A\u3010expiration\u3011\u8FC7\u671F\u65F6\u95F4\uFF0Cunix\u65F6\u95F4\u6233</li><li>nbf\uFF1A\u3010not before\u3011\u4F7F\u7528\u65F6\u95F4\u4E0D\u80FD\u65E9\u4E8E\u8FD9\u4E2A\u65F6\u95F4\uFF0Cunix\u65F6\u95F4\u6233</li><li>iat\uFF1A\u3010issued at\u3011\u7B7E\u53D1\u65F6\u95F4\uFF0C\u65F6\u95F4\u6233</li><li>jti\uFF1A\u3010<code>JWT</code> ID\u3011\u8BE5 <code>JWT</code> \u7684\u552F\u4E00\u7F16\u53F7</li></ul><p>\u8D1F\u8F7D\u7684\u5185\u5BB9\u8FD8\u53EF\u4EE5<strong>\u81EA\u5B9A\u4E49\u8FFD\u52A0</strong></p><p>JSON \u5185\u5BB9\u8981\u7ECF Base64 \u7F16\u7801\u751F\u6210\u5B57\u7B26\u4E32\u6210\u4E3A Payload</p><h3 id="signature-\u7B7E\u540D" tabindex="-1"><a class="header-anchor" href="#signature-\u7B7E\u540D" aria-hidden="true">#</a> Signature \u7B7E\u540D</h3><p>\u62E5\u6709\u8BE5\u90E8\u5206\u7684 <code>JWT</code> \u88AB\u79F0\u4E3A <code>JWS</code> \uFF1B\u6CA1\u6709\u5219\u79F0\u4E3A <code>nonsecure JWT</code> \u4E5F\u5C31\u662F\u4E0D\u5B89\u5168\u7684 <code>JWT</code> \uFF0C\u6B64\u65F6 Header \u4E2D\u58F0\u660E\u7684\u7B7E\u540D\u7B97\u6CD5\u4E3A None</p><p>\u8FD9\u4E2A\u90E8\u5206 Header \u4E0E Payload \u5148\u8FDB\u884C Base64 \u7F16\u7801\u518D\u901A\u8FC7 Header \u4E2D\u58F0\u660E\u7684\u52A0\u5BC6\u65B9\u5F0F\uFF0C\u4F7F\u7528\u5BC6\u94A5 secret \u8FDB\u884C\u52A0\u5BC6\uFF0C\u751F\u6210\u7B7E\u540D</p><p><code>JWS</code> \u7684\u4E3B\u8981\u76EE\u7684\u662F<strong>\u4FDD\u8BC1\u4E86\u6570\u636E</strong>\u5728\u4F20\u8F93\u8FC7\u7A0B\u4E2D<strong>\u4E0D\u88AB\u4FEE\u6539</strong>\uFF0C\u9A8C\u8BC1\u6570\u636E\u7684\u5B8C\u6574\u6027\u3002\u4F46\u7531\u4E8E\u4EC5\u91C7\u7528 Base64 \u5BF9\u6D88\u606F\u5185\u5BB9\u7F16\u7801(Header\u4E0EPayload)\uFF0C\u56E0\u6B64<strong>\u4E0D\u4FDD\u8BC1\u6570\u636E\u7684\u4E0D\u53EF\u6CC4\u9732\u6027\uFF0C\u6240\u4EE5\u4E0D\u9002\u5408\u7528\u4E8E\u4F20\u8F93\u654F\u611F\u6570\u636E</strong></p><h2 id="jwe" tabindex="-1"><a class="header-anchor" href="#jwe" aria-hidden="true">#</a> JWE</h2><p><code>JWE(JSON Web Encrypted)</code>\uFF1A<code>Encrypted JWT</code>\u90E8\u5206Payload<strong>\u7ECF\u8FC7\u52A0\u5BC6\u7684<code>JWT</code></strong>\uFF0C\u76F8\u5BF9\u4E8E <code>JWS</code>\uFF0C<code>JWE</code> \u5219<strong>\u540C\u65F6\u4FDD\u8BC1\u4E86\u5B89\u5168\u6027\u4E0E\u6570\u636E\u5B8C\u6574\u6027</strong></p><p><code>JWE</code>\u7531\u4E94\u90E8\u5206\u7EC4\u6210</p><p><img src="https://wingbun-notes-image.oss-cn-guangzhou.aliyuncs.com/images/20210228102124.png" alt=""></p><h3 id="\u751F\u6210\u6B65\u9AA4" tabindex="-1"><a class="header-anchor" href="#\u751F\u6210\u6B65\u9AA4" aria-hidden="true">#</a> \u751F\u6210\u6B65\u9AA4</h3><ol><li><code>JOSE Header</code> \u4E0E <code>JWS</code> \u7684 Header \u542B\u4E49\u76F8\u540C</li><li>\u751F\u6210\u4E00\u4E2A\u968F\u673A\u7684 <code>Content Encryption Key \uFF08CEK\uFF09</code></li><li>\u4F7F\u7528 RSAES-OAEP \u52A0\u5BC6\u7B97\u6CD5\uFF0C\u7528\u516C\u94A5\u52A0\u5BC6CEK\uFF0C\u751F\u6210<code>JWE Encrypted Key</code></li><li>\u751F\u6210<code>JWE</code>\u521D\u59CB\u5316\u5411\u91CF <code>Initialization Vector</code></li><li>\u4F7F\u7528 AES-GCM \u52A0\u5BC6\u7B97\u6CD5\u5BF9\u660E\u6587\u90E8\u5206\u8FDB\u884C\u52A0\u5BC6\u751F\u6210\u5BC6\u6587 <code>Ciphertext</code></li><li>\u7B97\u6CD5\u4F1A\u968F\u4E4B\u751F\u6210\u4E00\u4E2A128\u4F4D\u7684\u8BA4\u8BC1\u6807\u8BB0 <code>Authentication Tag</code></li><li>\u5BF95\u4E2A\u90E8\u5206\u5206\u522B\u8FDB\u884C Base64 \u7F16\u7801</li></ol><p>JWE\u7684\u8BA1\u7B97\u8FC7\u7A0B\u76F8\u5BF9\u7E41\u7410\uFF0C\u4E0D\u591F\u8F7B\u91CF\u7EA7\uFF0C\u56E0\u6B64<strong>\u9002\u5408\u4E0E\u6570\u636E\u4F20\u8F93\u800C\u975EToken\u8BA4\u8BC1</strong>\uFF0C\u4F46\u8BE5\u534F\u8BAE\u4E5F\u8DB3\u591F\u5B89\u5168\u53EF\u9760\uFF0C\u7528\u7B80\u77ED\u5B57\u7B26\u4E32\u63CF\u8FF0\u4E86\u4F20\u8F93\u5185\u5BB9\uFF0C<strong>\u517C\u987E\u6570\u636E\u7684\u5B89\u5168\u6027\u4E0E\u5B8C\u6574\u6027</strong></p><h2 id="jwt\u6D41\u7A0B" tabindex="-1"><a class="header-anchor" href="#jwt\u6D41\u7A0B" aria-hidden="true">#</a> JWT\u6D41\u7A0B</h2><p>Client\uFF1A\u5BA2\u6237\u7AEF</p><p>Auth Server\uFF1A\u8BA4\u8BC1\u670D\u52A1\u5668</p><p>Resource Server\uFF1A\u8D44\u6E90\u670D\u52A1\u5668</p><p>JWT\u7B7E\u540D\u7B97\u6CD5\u4E00\u822C\u4F1A\u6709\u4E24\u4E2A\u9009\u62E9\uFF1AHS256\uFF08HMACSHA256\u3001\u5BF9\u79F0\u7B97\u6CD5\uFF09\u548CRS256\uFF08RSASHA256\u3001\u975E\u5BF9\u79F0\u7B97\u6CD5\uFF09</p><h3 id="hmac\u6D41\u7A0B" tabindex="-1"><a class="header-anchor" href="#hmac\u6D41\u7A0B" aria-hidden="true">#</a> HMAC\u6D41\u7A0B</h3><p><img src="https://wingbun-notes-image.oss-cn-guangzhou.aliyuncs.com/images/20210801193743.png" alt=""></p><p>Auth Server\u9700\u8981\u4E0EResource Server\u63D0\u524D\u5546\u5B9A\u597D\u7528\u4E8E\u7B7E\u540D\u3001\u6821\u9A8C\u7684\u5BC6\u94A5secret\uFF0C\u5E76\u4E14<strong>\u5FC5\u987B\u4FDD\u8BC1secret\u4E0D\u80FD\u6CC4\u6F0F</strong>\uFF0C\u5426\u5219\u4E0D\u5B89\u5168\uFF0C\u653B\u51FB\u8005\u53EF\u4EE5\u5229\u7528\u8FD9\u4E2Asecret\u6765\u4F2A\u9020\u4EE4\u724C</p><h3 id="rsa\u6D41\u7A0B" tabindex="-1"><a class="header-anchor" href="#rsa\u6D41\u7A0B" aria-hidden="true">#</a> RSA\u6D41\u7A0B</h3><p><img src="https://wingbun-notes-image.oss-cn-guangzhou.aliyuncs.com/images/20210801193801.png" alt=""></p><p>Auth Server\u5728\u751F\u6210JWT\u7684\u65F6\u5019\u4F7F\u7528\u79C1\u94A5\u8FDB\u884C\u52A0\u5BC6\uFF0CResource Server\u5728\u6821\u9A8CJWT\u7684\u65F6\u5019\u4F7F\u7528\u516C\u94A5\u8FDB\u884C\u89E3\u5BC6\u3002<strong>RSA\u65B9\u5F0F\u66F4\u52A0\u5B89\u5168</strong>\uFF0Csecret\u9700\u8981Auth Server\u548CResource Server\u53CC\u65B9\u90FD\u4FDD\u5BC6\uFF0C\u79C1\u94A5\u53EA\u5B58\u5728\u4E8EAuth Server\uFF0C\u53EA\u9700\u4FDD\u8BC1\u79C1\u94A5\u4E0D\u88AB\u6CC4\u6F0F\u5373\u53EF</p>',41),r=[i];function n(t,s){return o(),d("div",null,r)}var p=e(a,[["render",n],["__file","JWT.html.vue"]]);export{p as default};
