import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";import{o as a,c as i,d as r}from"./app.d314e544.js";const s={},t=r('<h1 id="on-premises" tabindex="-1"><a class="header-anchor" href="#on-premises" aria-hidden="true">#</a> On-Premises</h1><p>\u672C\u5730\u90E8\u7F72</p><h1 id="iaas" tabindex="-1"><a class="header-anchor" href="#iaas" aria-hidden="true">#</a> IaaS</h1><p><strong>Infrastructure as a Service</strong> - \u57FA\u7840\u8BBE\u65BD\u5373\u670D\u52A1 \u4ECE\u86CB\u7CD5\u5E97\u4E2D\u76F4\u63A5\u8D2D\u4E70\u6210\u54C1\uFF0C\u56DE\u5BB6\u540E\u7528\u5FAE\u6CE2\u7089\u8FDB\u884C\u70D8\u7119\u4E4B\u540E\u5C31\u53EF\u4EE5\u5728\u81EA\u5DF1\u7684\u9910\u684C\u4E0A\u5403\uFF0C\u6574\u4E2A\u6D41\u7A0B\u4F60\u4EC5\u9700\u8981\u4E00\u4E2A\u86CB\u7CD5\u6210\u54C1\u7684\u4F9B\u5E94\u5546\u3002\uFF08\u5BA2\u6237\u4E0D\u7528\u81EA\u5DF1\u6784\u5EFA\u4E00\u4E2A\u6570\u636E\u4E2D\u5FC3\uFF0C\u800C\u662F\u901A\u8FC7\u79DF\u7528\u7684\u65B9\u5F0F\u6765\u4F7F\u7528\u57FA\u7840\u8BBE\u65BD\u670D\u52A1\uFF0C\u5305\u62EC\u670D\u52A1\u5668\u3001\u5B58\u50A8\u548C\u7F51\u7EDC\u7B49\u3002\uFF09</p><h1 id="paas" tabindex="-1"><a class="header-anchor" href="#paas" aria-hidden="true">#</a> PaaS</h1><p><strong>Platform as a Service</strong> - \u5E73\u53F0\u5373\u670D\u52A1 \u6253\u5F00\u67D0\u5916\u5356App\u76F4\u63A5\u53EB\u5916\u5356\u5C06\u86CB\u7CD5\u9001\u5230\u5BB6\u4E2D\uFF0C\u6446\u5728\u9910\u684C\u4E0A\u5C31\u53EF\u4EE5\u5403\u4E86\uFF0C\u6574\u4E2A\u6D41\u7A0B\u4F60\u9700\u8981\u63D0\u4F9B\u4E00\u4E2A\u5403\u86CB\u7CD5\u7684\u5730\u65B9\u3002\uFF08\u5C06\u4E00\u4E2A\u5B8C\u6574\u7684\u8F6F\u4EF6\u7814\u53D1\u548C\u90E8\u7F72\u5E73\u53F0\uFF0C\u5305\u62EC\u5E94\u7528\u8BBE\u8BA1\u3001\u5E94\u7528\u5F00\u53D1\u3001\u5E94\u7528\u6D4B\u8BD5\u548C\u5E94\u7528\u6258\u7BA1\uFF0C\u90FD\u4F5C\u4E3A\u4E00\u79CD\u670D\u52A1\u63D0\u4F9B\u7ED9\u5BA2\u6237\u3002\uFF09</p><h1 id="saas" tabindex="-1"><a class="header-anchor" href="#saas" aria-hidden="true">#</a> SaaS</h1><p><strong>Software as a Service</strong> - \u8F6F\u4EF6\u5373\u670D\u52A1 \u76F4\u63A5\u5728\u86CB\u7CD5\u5E97\u4E2D\u5403\u86CB\u7CD5\uFF0C\u968F\u610F\u6311\u9009\u4F60\u559C\u6B22\u7684\u86CB\u7CD5\u7C7B\u578B\uFF0C\u5E76\u4E14\u65E0\u9700\u51C6\u5907\u4EFB\u4F55\u7269\u6599\uFF0C\u5305\u62EC\u86CB\u7CD5\u6210\u54C1\u3001\u5FAE\u6CE2\u7089\u3001\u4EE5\u53CA\u9910\u684C\uFF0C\u86CB\u7CD5\u5E97\u5168\u90FD\u4F1A\u4E3A\u4F60\u63D0\u4F9B\uFF0C\u6574\u4E2A\u6D41\u7A0B\u4EC5\u9700\u5B89\u5FC3\u7684\u5750\u4E0B\u5403\u86CB\u7CD5\u5373\u53EF\u3002\uFF08\u5C06\u5E94\u7528\u8F6F\u4EF6\u7EDF\u4E00\u90E8\u7F72\u5728\u5382\u5546\u7684\u670D\u52A1\u5668\u4E0A\uFF0C\u5BA2\u6237\u53EF\u4EE5\u6839\u636E\u5B9E\u9645\u9700\u6C42\uFF0C\u4EFB\u610F\u9009\u62E9\u6240\u9700\u7684\u5E94\u7528\u8F6F\u4EF6\u670D\u52A1\uFF0C\u901A\u8FC7\u4E92\u8054\u7F51\u83B7\u5F97Saas\u5E73\u53F0\u4F9B\u5E94\u5546\u63D0\u4F9B\u7684\u670D\u52A1\u3002\uFF09</p><p><img src="https://wingbun-notes-image.oss-cn-guangzhou.aliyuncs.com/images/20200909144509.png" alt=""></p><hr><p>\u5047\u8BBE\u4F60\u60F3\u4ECE\u65E0\u5230\u6709\u529E\u4E2A\u517B\u732A\u573A\uFF0C\u8981\u517B\u732A\u5F53\u7136\u8981\u6709\u5382\u623F\u54AF\uFF0C\u6B64\u65F6\u6709\u4E24\u79CD\u9009\u62E9\uFF1A</p><ol><li>\u79DF\u4E00\u5757\u5730\uFF0C\u4ECE\u4E00\u7816\u4E00\u74E6\u5F00\u59CB\u5EFA\uFF1B</li><li>\u79DF\u7528\u522B\u4EBA\u5DF2\u7ECF\u5EFA\u597D\u7684\u5382\u623F\u3002</li></ol><p><strong>\u81EA\u5DF1\u5EFA\u5382\u623F\u7684\u8BDD\uFF0C\u79DF\u7ED9\u4F60\u5730\u7684\u4E00\u65B9\u5C31\u76F8\u5F53\u4E8EIaaS\u63D0\u4F9B\u8005\uFF0C\u63D0\u4F9B\u7ED9\u4F60\u7684Infrastructure\u5C31\u662F\u5730</strong>\u3002 \u5176\u4F18\u70B9\u662F\u4F60\u53EF\u4EE5\u5728\u5730\u5141\u8BB8\u7684\u8303\u56F4\u5185\u968F\u610F\u5EFA\u9020\uFF0C\u9664\u4E86\u517B\u732A\u573A\uFF0C\u517B\u9E21\u573A\u4E5F\u662F\u53EF\u4EE5\u5EFA\u7684\uFF0C\u81EA\u7531\u5EA6\u5F88\u5927\uFF0C\u4F60\u53EF\u4EE5\u6309\u7167\u732A\u7684\u517B\u6B96\u60C5\u51B5\uFF0C\u7ED9\u5B83\u4EEC\u5EFA\u516B\u4EBA\u95F4\uFF0C\u56DB\u4EBA\u95F4\uFF0C\u9AD8\u4F4E\u5E8A\uFF0C\u7B49\u7B49\u3002 \u7F3A\u70B9\u662F\u9664\u4E86\u7ED9\u4F60\u5730\u4E4B\u5916\u5565\u90FD\u6CA1\u6709\uFF0C\u6C34\u7535\u4E0D\u901A\uFF0C\u4E00\u7816\u4E00\u74E6\u90FD\u8981\u4F60\u4EB2\u81EA\u7B79\u5907\u3002</p><p><strong>\u79DF\u7528\u522B\u4EBA\u5382\u623F\u7684\u8BDD\u5C31\u548CPaaS\u7C7B\u4F3C\uFF0C\u5382\u623F\u5C31\u662FPlatform</strong>\u3002 \u5176\u4F18\u70B9\u662F\u5DF2\u7ECF\u7ED9\u4F60\u63D0\u4F9B\u597D\u4E86\u73B0\u6210\u7684\u5382\u623F\u8BBE\u65BD\uFF0C\u57FA\u672C\u7684\u6C34\u7535\u4E5F\u6709\u4E86\uFF0C\u53EA\u9700\u8981\u628A\u4F60\u7684\u4E1A\u52A1\uFF0C\u4E5F\u5C31\u662F\u732A\uFF0C\u653E\u8FDB\u6765\u5C31\u53EF\u4EE5\uFF0C\u4EE5\u540E\u517B\u6B96\u89C4\u6A21\u6269\u5927\u4E86\u7684\u8BDD\uFF0C\u8FD8\u53EF\u4EE5\u518D\u79DF\u4E00\u4E2A\u5382\u623F\u3002 \u7F3A\u70B9\u662F\u5382\u623F\u91CC\u732A\u820D\u7684\u5927\u5C0F\uFF0C\u8BBE\u65BD\u4F4D\u7F6E\u7B49\u5DF2\u7ECF\u56FA\u5B9A\u4E86\uFF0C\u4F60\u4E0D\u80FD\u505A\u592A\u591A\u7684\u81EA\u5B9A\u4E49\uFF0C\u4E00\u4E2A\u732A\u820D\u5173\u4E24\u5934\u732A\u5ACC\u6D6A\u8D39\uFF0C\u4E09\u5934\u732A\u53C8\u592A\u6324\uFF0C\u67D0\u4E00\u5929\u5F15\u8FDB\u4E00\u4E2A\u65B0\u54C1\u79CD\uFF0C\u8FD9\u4E2A\u54C1\u79CD\u7684\u732A\u5E73\u65E5\u559C\u6B22\u901A\u900F\u5F0F\u7684LOFT\u732A\u820D\uFF0C\u800C\u4F60\u73B0\u5728\u7684\u732A\u820D\u90FD\u662F\u56DB\u9762\u9AD8\u5899\u548C\u6545\u5BAB\u4E00\u6837\uFF0C\u8FD9\u6837\u4F60\u7684\u65B0\u4E1A\u52A1\u4E5F\u5C31\u662F\u65B0\u54C1\u79CD\u7684\u732A\u5C31\u4E0D\u80FD\u987A\u5229\u5730\u90E8\u7F72\u5728\u732A\u820D\u91CC\u4E86\u3002</p><hr><p>\u517B\u732A\u573A\u529E\u597D\u4E86\uFF0C\u6B64\u65F6\u65B0\u7684\u95EE\u9898\u53C8\u63A5\u8E35\u800C\u81F3\uFF1A\u732A\u6709\u7684\u65F6\u5019\u4F1A\u751F\u75C5\uFF0C\u4F46\u81EA\u5DF1\u53C8\u6CA1\u6709\u4E13\u4E1A\u7684\u517D\u533B\uFF0C\u600E\u4E48\u529E\u5462\uFF1F<strong>\u8FD9\u4E2A\u65F6\u5019\u6709\u4E00\u4E2A\u53EB\u79BD\u517D\u5927\u4FDD\u5065\u7684\u673A\u6784\uFF0C\u4E13\u95E8\u4E3A\u517B\u6B96\u573A\u63D0\u4F9B\u732A\u75C5\u9632\u6CBB\u7684\u670D\u52A1\uFF0C\u8FD9\u4E2A\u673A\u6784\u5C31\u662FSaaS\u7684\u89D2\u8272</strong>\u3002\u9664\u4E86\u8FD9\u4E2A\u673A\u6784\u4EE5\u5916\uFF0C\u8FD8\u6709\u63D0\u4F9B\u751F\u732A\u9500\u552E\u670D\u52A1\uFF0C\u6BCD\u732A\u914D\u79CD\u670D\u52A1\u7B49\u7B49\u7684\u673A\u6784\uFF0C\u4F60\u5C31\u53EF\u4EE5\u628A\u517B\u732A\u573A\u91CC\u7684\u8FD9\u4E9B\u4E1A\u52A1\u4EA4\u7ED9\u4E0A\u8FF0\u673A\u6784\u5904\u7406\uFF0C\u800C\u4F60\uFF0C\u4F5C\u4E3A\u4E00\u4E2A\u517B\u732A\u573A\u7684\u573A\u957F\uFF0C\u53EA\u9700\u8981\u6839\u636E\u4E0D\u540C\u7684\u60C5\u51B5\uFF0C\u9009\u62E9\u628A\u732A\u517B\u5728\u4EC0\u4E48\u5730\u65B9\u5C31\u53EF\u4EE5\u4E86\u3002</p><hr><h1 id="soa" tabindex="-1"><a class="header-anchor" href="#soa" aria-hidden="true">#</a> SOA</h1><p>Service-Oriented Architecture\uFF0C\u9762\u5411\u670D\u52A1\u7684\u67B6\u6784</p><p><img src="https://wingbun-notes-image.oss-cn-guangzhou.aliyuncs.com/images/880309-20170514190347066-1095632262.png" alt="img"></p><ul><li><p>SOA\u5E2E\u52A9\u6211\u4EEC\u628A\u670D\u52A1\u4E4B\u95F4\u8C03\u7528\u7684\u5173\u7CFB\u6CBB\u7406\u8D77\u6765\uFF0C\u4ECE\u4EE5\u524D\u7684\u4E92\u76F8\u4EA4\u4E92\uFF0C\u5230\u73B0\u5728\u7684\u53EA\u5BF9\u6570\u636E\u603B\u7EBF\u8FDB\u884C\u4EA4\u4E92\uFF0C\u63D0\u4F9B\u4E00\u4E2A\u7EDF\u4E00\u7684\u6807\u51C6\uFF0C\u8FD9\u6837\u7CFB\u7EDF\u5C31\u53D8\u5F97\u7EDF\u4E00\u8D77\u6765\u3002</p><ul><li><p>\u7EDF\u4E00\u6807\u51C6\uFF1A\u5404\u7CFB\u7EDF\u7684\u534F\u8BAE\u3001\u5730\u5740\u3001\u4EA4\u4E92\u65B9\u5F0F</p></li><li><p>\u65B0\u7684\u4EA4\u4E92\u65B9\u5F0F\uFF1A\u5404\u4E2A\u7CFB\u7EDF\u5206\u522B\u6839\u636E\u7EDF\u4E00\u6807\u51C6\u5411\u6570\u636E\u603B\u7EBF\u8FDB\u884C\u6CE8\u518C\uFF0C\u5B50\u7CFB\u7EDF\u8C03\u7528\u5176\u4ED6\u5B50\u7CFB\u7EDF\u65F6\uFF0C\u53EA\u627E\u6570\u636E\u603B\u7EBF\uFF0C\u6570\u636E\u603B\u7EBF\u518D\u6839\u636E\u7EDF\u4E00\u6807\u51C6\u627E\u5176\u4ED6\u5B50\u7CFB\u7EDF\uFF0C\u6570\u636E\u603B\u7EBF\u5728\u8FD9\u91CC\u5145\u5F53\u4E00\u4E2A\u53EA\u8DEF\u4EBA\u7684\u4F5C\u7528</p></li><li><p>\u6570\u636E\u603B\u7EBF\uFF1A\u5145\u5F53\u6570\u636E\u901A\u9053\u7684\u4F5C\u7528\uFF0C\u6570\u636E\u603B\u7EBF\u4E0D\u5173\u5FC3\u4E1A\u52A1\uFF0C\u6570\u636E\u603B\u7EBF\u6839\u636E\u7ED9\u7684\u5730\u5740\u548C\u534F\u8BAE\u53BB\u8C03\u670D\u52A1</p></li></ul></li><li><p>SOA\u597D\u5904</p><ul><li>\u964D\u4F4E\u7528\u6237\u6210\u672C\uFF0C\u7528\u6237\u4E0D\u9700\u8981\u5173\u5FC3\u5982\u4F55\u8C03\u7528\u670D\u52A1\uFF0C\u53EA\u8981\u901A\u8FC7\u7EDF\u4E00\u6807\u51C6\u627E\u6570\u636E\u603B\u7EBF\u5C31\u53EF\u4EE5</li><li>\u4F4E\u8026\u5408\uFF0C\u670D\u52A1\u4E4B\u95F4\u5173\u7CFB\u7B80\u5355</li><li>\u56E0\u4E3A\u670D\u52A1\u9700\u8981\u5411\u6570\u636E\u603B\u7EBF\u6CE8\u518C\uFF0C\u6240\u4EE5\u53EF\u4EE5\u53CA\u65F6\u8BC6\u522B\u54EA\u4E9B\u670D\u52A1\u6709\u95EE\u9898</li></ul></li><li><p>SOA\u7F3A\u70B9\uFF1A\u63D0\u793A\u4E86\u7CFB\u7EDF\u7684\u590D\u6742\u7A0B\u5EA6\uFF0C\u6027\u80FD\u6709\u76F8\u5E94\u5F71\u54CD</p></li><li><p>SOA\u4E0E\u5FAE\u670D\u52A1\u533A\u522B</p><ul><li>SOA\u4FA7\u91CD\u670D\u52A1\u7684\u91CD\u7528\u6027\u548C\u89E3\u51B3\u4FE1\u606F\u5B64\u5C9B\u95EE\u9898</li><li>\u5FAE\u670D\u52A1\u4FA7\u91CD\u4E1A\u52A1\u4E4B\u95F4\u7684\u89E3\u8026</li><li>\u5FAE\u670D\u52A1\u662FSOA\u67B6\u6784\u6F14\u8FDB\u7684\u7ED3\u679C\u3002\u968F\u7740\u4E92\u8054\u7F51\u7684\u53D1\u5C55\uFF0C\u590D\u6742\u7684\u5E73\u53F0\u3001\u4E1A\u52A1\u7684\u51FA\u73B0\uFF0C\u5BFC\u81F4SOA\u67B6\u6784\u5411\u66F4\u7EC6\u7C92\u5EA6\u3001\u66F4\u901A\u8FC7\u5316\u7A0B\u5EA6\u53D1\u5C55\uFF0C\u5C31\u6210\u4E86\u6240\u8C13\u7684\u5FAE\u670D\u52A1\u4E86\uFF0C\u5FAE\u670D\u52A1\u662FSOA\u53D1\u5C55\u51FA\u6765\u7684\u4EA7\u7269\uFF0C\u5B83\u662F\u4E00\u79CD\u6BD4\u8F83\u73B0\u4EE3\u5316\u7684\u7EC6\u7C92\u5EA6\u7684SOA\u5B9E\u73B0\u65B9\u5F0F</li></ul></li></ul><p><img src="https://wingbun-notes-image.oss-cn-guangzhou.aliyuncs.com/images/09fb5b91107b4a87b59cafc3366bd088.jpeg" alt="img"></p><h1 id="\u5FAE\u670D\u52A1" tabindex="-1"><a class="header-anchor" href="#\u5FAE\u670D\u52A1" aria-hidden="true">#</a> \u5FAE\u670D\u52A1</h1><p>\u5C06\u6BCF\u4E00\u4E2A\u5177\u4F53\u4E1A\u52A1\u670D\u52A1\u6784\u6210\u53EF\u72EC\u7ACB\u8FD0\u884C\u7684\u5FAE\u670D\u52A1\uFF0C\u6BCF\u4E2A\u670D\u52A1\u53EA\u5173\u6CE8\u67D0\u4E2A\u7279\u5B9A\u7684\u529F\u80FD\uFF0C\u670D\u52A1\u4E4B\u95F4\u91C7\u7528\u8F7B\u91CF\u7EA7\u901A\u4FE1\u673A\u5236REST API\u8FDB\u884C\u901A\u4FE1\u3002</p><p>\u4F18\u70B9\uFF1A</p><ul><li>\u590D\u6742\u5EA6\u53EF\u63A7\u3002\u901A\u8FC7\u5BF9\u516C\u5171\u4E1A\u52A1\u670D\u52A1\u8FDB\u884C\u66F4\u7EC6\u7C92\u5EA6\u5730\u62C6\u5206\uFF0C\u6240\u4EE5\u6BCF\u4E2A\u670D\u52A1\u53EA\u5173\u6CE8\u7279\u5B9A\u7684\u4E1A\u52A1\u9886\u57DF\uFF0C\u6709\u6E05\u6670\u7684\u670D\u52A1\u8FB9\u754C</li><li>\u72EC\u7ACB\u90E8\u7F72\u3002\u6BCF\u4E00\u4E2A\u5FAE\u670D\u52A1\u90FD\u662F\u4E00\u4E2A\u72EC\u7ACB\u8FD0\u884C\u7684\u8FDB\u7A0B\uFF0C\u67D0\u4E2A\u670D\u52A1\u53D1\u751F\u53D8\u66F4\u4E0D\u9700\u8981\u91CD\u65B0\u7F16\u8BD1\u6574\u4E2A\u5E94\u7528</li><li>\u5BB9\u9519\u6027\u3002\u5F53\u67D0\u4E00\u4E2A\u670D\u52A1\u53D1\u751F\u6545\u969C\uFF0C\u53EF\u4EE5\u628A\u9519\u8BEF\u9694\u79BB\u5728\u5355\u4E2A\u670D\u52A1\u4E2D</li><li>\u53EF\u6269\u5C55\u6027\u5F3A\u3002\u589E\u52A0\u5355\u4E2A\u670D\u52A1\u7684\u96C6\u7FA4\u89C4\u6A21\uFF0C\u63D0\u5347\u90E8\u7F72\u8BE5\u670D\u52A1\u8282\u70B9\u7684\u786C\u4EF6\u914D\u7F6E</li></ul><p>\u7F3A\u70B9\uFF1A</p><ul><li>\u6545\u969C\u6392\u67E5\u3002\u4E00\u6B21\u8BF7\u6C42\u53EF\u80FD\u4F1A\u7ECF\u5386\u591A\u4E2A\u4E0D\u540C\u7684\u5FAE\u670D\u52A1\u7684\u591A\u6B21\u4EA4\u4E92\uFF0C\u4EA4\u4E92\u94FE\u8DEF\u53EF\u80FD\u8F83\u957F\uFF0C\u5B9A\u4F4D\u95EE\u9898\u53EF\u80FD\u6BD4\u8F83\u56F0\u96BE</li><li>\u670D\u52A1\u76D1\u63A7\u3002\u5FAE\u670D\u52A1\u67B6\u6784\u4E2D\uFF0C\u4E0D\u4EC5\u8981\u5BF9\u6574\u4E2A\u94FE\u8DEF\u8FDB\u884C\u76D1\u63A7\uFF0C\u8FD8\u9700\u8981\u5BF9\u5355\u4E2A\u5FAE\u670D\u52A1\u8FDB\u884C\u76D1\u63A7\uFF0C\u76D1\u63A7\u5F00\u9500\u5927</li><li>\u7F51\u7EDC\u95EE\u9898\u3002\u5FAE\u670D\u52A1\u67B6\u6784\u4E2D\u670D\u52A1\u4E4B\u95F4\u9700\u8981\u7F51\u7EDC\u901A\u4FE1\uFF0C\u65E0\u6CD5\u907F\u514D\u7F51\u7EDC\u5EF6\u8FDF\u4E0E\u7F51\u7EDC\u6545\u969C</li><li>\u8FD0\u7EF4\u6210\u672C\u3002\u9700\u8981\u89E3\u51B3\u5355\u4E2A\u670D\u52A1\u6D41\u91CF\u6FC0\u589E\u65F6\u5982\u4F55\u5FEB\u901F\u6269\u5BB9\u3001\u670D\u52A1\u62C6\u5206\u4E4B\u540E\u5BFC\u81F4\u6545\u969C\u70B9\u589E\u591A\u5982\u4F55\u5904\u7406\u7B49\u95EE\u9898</li></ul><p><img src="https://gitee.com/ngwingbun/pic/raw/master/image-20201201103602173.png" alt="image-20201201103602173"></p><h1 id="webservice" tabindex="-1"><a class="header-anchor" href="#webservice" aria-hidden="true">#</a> WebService</h1><p>\u4F4E\u8026\u5408\u7684\uFF0C\u81EA\u5305\u542B\u7684\u3001\u57FA\u4E8E\u53EF\u7F16\u7A0B\u7684web\u7684\u5E94\u7528\u7A0B\u5E8F\uFF0C\u53EF\u4F7F\u7528\u5F00\u653E\u7684<strong>XML</strong>\u6807\u51C6\u6765\u63CF\u8FF0\u3001\u53D1\u5E03\u3001\u53D1\u73B0\u3001\u534F\u8C03\u548C\u914D\u7F6E\u8FD9\u4E9B\u5E94\u7528\u7A0B\u5E8F\uFF0C\u7528\u4E8E\u5F00\u53D1\u5206\u5E03\u5F0F\u7684\u4E92\u64CD\u4F5C\u7684\u5E94\u7528\u7A0B\u5E8F</p><p>WebService\u662F\u4E00\u79CD\u6280\u672F\uFF0C\u53EA\u8981\u662F\u80FD\u591F\u5E73\u53F0\u72EC\u7ACB\uFF0C<strong>\u65E0\u5173\u8BED\u8A00\uFF0C\u65E0\u5173\u64CD\u4F5C\u7CFB\u7EDF\uFF0C\u57FA\u4E8EXML\u4F5C\u4E3A\u6570\u636E\u4EA4\u6362\u683C\u5F0F</strong>\u7684\u5E94\u7528\u7A0B\u5E8F\u90FD\u53EF\u4EE5\u53EB\u505AWebService\u3002</p><p>\u8981\u5B9E\u73B0WebService\uFF0C\u9700\u8981\u4E00\u5957\u534F\u8BAE\u6765\u652F\u6301\uFF08WebService\u4E09\u8981\u7D20\uFF1ASOAP\u3001WSDL\u3001UDDI\uFF09</p><h2 id="soap" tabindex="-1"><a class="header-anchor" href="#soap" aria-hidden="true">#</a> SOAP</h2><p>SOAP\uFF08Simple Object Access Protocol\uFF1A\u7B80\u5355\u5BF9\u8C61\u8BBF\u95EE\u534F\u8BAE\uFF09\u662F\u4E00\u79CD\u8F7B\u91CF\u7B80\u5355\u7684\u3001\u57FA\u4E8EXML\u7684\u4EA4\u6362\u6570\u636E\u7684\u534F\u8BAE\u89C4\u8303\u3002 \uFF08<strong>\u7B80\u5355\u6765\u8BF4\uFF1ASOAP=HTTP+XML\uFF0CHTTP\u662F\u57FA\u4E8ETCP\u7684\u8D85\u6587\u672C\u4F20\u9001\u534F\u8BAE</strong>\uFF09</p><h2 id="wsdl" tabindex="-1"><a class="header-anchor" href="#wsdl" aria-hidden="true">#</a> WSDL</h2><p>WSDL\uFF08Web Service Describe Language\uFF1AWeb Service \u63CF\u8FF0\u8BED\u8A00\uFF09\u7528\u4E8E\u63CF\u8FF0Web Service\u53CA\u5176\u51FD\u6570\u3001\u53C2\u6570\u548C\u8FD4\u56DE\u503C\uFF08\u4E5F\u5C31\u662F\u63CF\u8FF0\u5982\u4F55\u8BBF\u95EE\u5177\u4F53\u7684\u63A5\u53E3\uFF09</p><h2 id="uddi" tabindex="-1"><a class="header-anchor" href="#uddi" aria-hidden="true">#</a> UDDI</h2><p>UDDI \u7684\u76EE\u7684\u662F\u4E3A\u7535\u5B50\u5546\u52A1\u5EFA\u7ACB\u6807\u51C6\uFF1BUDDI\u662F\u4E00\u5957\u57FA\u4E8EWeb\u7684\u3001\u5206\u5E03\u5F0F\u7684\u3001\u4E3AWeb Service\u63D0\u4F9B\u7684\u3001\u4FE1\u606F\u6CE8\u518C\u4E2D\u5FC3\u7684\u5B9E\u73B0\u6807\u51C6\u89C4\u8303\uFF0C\u540C\u65F6\u4E5F\u5305\u542B\u4E00\u7EC4\u4F7F\u4F01\u4E1A\u80FD\u5C06\u81EA\u8EAB\u63D0\u4F9B\u7684Web Service\u6CE8\u518C\uFF0C\u4EE5\u4F7F\u522B\u7684\u4F01\u4E1A\u80FD\u591F\u53D1\u73B0\u7684\u8BBF\u95EE\u534F\u8BAE\u7684\u5B9E\u73B0\u6807\u51C6\u3002\uFF08<strong>\u7B80\u5355\u4E00\u53E5\u8BDD\u6982\u62EC\u5C31\u662F\uFF1A\u7528\u6765\u7BA1\u7406\uFF0C\u5206\u53D1\uFF0C\u67E5\u8BE2WebService</strong>\uFF09</p><h1 id="rpc" tabindex="-1"><a class="header-anchor" href="#rpc" aria-hidden="true">#</a> RPC</h1><p>RPC\uFF08Remote Procedure Call\uFF1A\u8FDC\u7A0B\u8FC7\u7A0B\u8C03\u7528\uFF09\u5C31\u662F\u4ECE\u4E00\u53F0\u673A\u5668\uFF08\u5BA2\u6237\u7AEF\uFF09\u4E0A\u901A\u8FC7\u53C2\u6570\u4F20\u9012\u7684\u65B9\u5F0F\u8C03\u7528\u53E6\u4E00\u53F0\u673A\u5668\uFF08\u670D\u52A1\u5668\uFF09\u4E0A\u7684\u4E00\u4E2A\u51FD\u6570\u6216\u65B9\u6CD5\uFF08\u53EF\u4EE5\u7EDF\u79F0\u4E3A\u670D\u52A1\uFF09\u5E76\u5F97\u5230\u8FD4\u56DE\u7684\u7ED3\u679C</p><h1 id="rmi" tabindex="-1"><a class="header-anchor" href="#rmi" aria-hidden="true">#</a> RMI</h1><p>RMI\uFF08Remote Method Invocation\uFF1A\u8FDC\u7A0B\u65B9\u6CD5\u8C03\u7528\uFF09\u57FA\u4E8EJava\u8FDC\u7A0B\u65B9\u6CD5\u534F\u8BAE\u548Cjava\u7684\u539F\u751F\u5E8F\u5217\u5316\u7684RPC\u3002</p><h1 id="rest" tabindex="-1"><a class="header-anchor" href="#rest" aria-hidden="true">#</a> REST</h1><p>REST\uFF08Representational State Transfer\uFF1A\u8868\u73B0\u6027\u72B6\u6001\u8F6C\u6362\uFF09\u4E0D\u662F\u534F\u8BAE\u4E5F\u4E0D\u662F\u89C4\u8303\uFF0C\u800C\u662F\u4E00\u79CD\u670D\u52A1\u901A\u8BAF\u7684\u8BBE\u8BA1\u98CE\u683C</p><p>RPC\u662F\u9762\u5411\u670D\u52A1\u7684\uFF0C\u5E76\u5173\u6CE8\u4E8E\u884C\u4E3A\u548C\u52A8\u4F5C\uFF1B\u800CREST\u662F\u9762\u5411\u8D44\u6E90\u7684\uFF0C\u5F3A\u8C03\u63CF\u8FF0\u5E94\u7528\u7A0B\u5E8F\u7684\u4E8B\u7269\u548C\u540D\u8BCD</p><h2 id="restful" tabindex="-1"><a class="header-anchor" href="#restful" aria-hidden="true">#</a> Restful</h2><p>Restful\u662F\u4E00\u79CD\u67B6\u6784\u8BBE\u8BA1\u98CE\u683C\uFF0C\u63D0\u4F9B\u4E86\u8BBE\u8BA1\u539F\u5219\u548C\u7EA6\u675F\u6761\u4EF6\uFF0C\u800C\u4E0D\u662F\u67B6\u6784\uFF0C\u800C\u6EE1\u8DB3\u8FD9\u4E9B\u7EA6\u675F\u6761\u4EF6\u548C\u539F\u5219\u7684\u5E94\u7528\u7A0B\u5E8F\u6216\u8BBE\u8BA1\u5C31\u662F Restful\u67B6\u6784\u6216\u670D\u52A1</p><p>RESTful\u7684\u6838\u5FC3\u5C31\u662F\u540E\u7AEF\u5C06\u8D44\u6E90\u53D1\u5E03\u4E3AURI\uFF0C\u524D\u7AEF\u901A\u8FC7URI\u8BBF\u95EE\u8D44\u6E90\uFF0C\u5E76\u901A\u8FC7HTTP\u52A8\u8BCD\u8868\u793A\u8981\u5BF9\u8D44\u6E90\u8FDB\u884C\u7684\u64CD\u4F5C</p><p>SOA\u548C\u5FAE\u670D\u52A1\u67B6\u6784\u90FD\u662F\u4E00\u79CD\u7EC4\u4EF6\u6A21\u578B\uFF0C\u4E00\u79CD\u67B6\u6784\u65B9\u5F0F\uFF0C\u4E0D\u4F9D\u8D56\u4E8E\u4EFB\u4F55\u6280\u672F\uFF0C\u56E0\u6B64SOAP\u3001RPC\u3001REST\u662F\u5BF9SOA\u548C\u5FAE\u670D\u52A1\u67B6\u6784\u7684\u7EC4\u4EF6\u6216\u670D\u52A1\u4E4B\u95F4\u901A\u4FE1\u65B9\u5F0F\u7684\u4E0D\u540C\u5B9E\u73B0\u3002</p>',50),n=[t];function l(h,p){return a(),i("div",null,n)}var o=e(s,[["render",l],["__file","\u670D\u52A1.html.vue"]]);export{o as default};
