import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";import{o as e,c as o,d as i}from"./app.8326e8d1.js";const n={},t=i('<h1 id="java\u7269\u7406\u5185\u5B58\u6A21\u578B" tabindex="-1"><a class="header-anchor" href="#java\u7269\u7406\u5185\u5B58\u6A21\u578B" aria-hidden="true">#</a> Java\u7269\u7406\u5185\u5B58\u6A21\u578B</h1><blockquote><p>\u672C\u6587\u4ECE\u5806\u6808\u89D2\u5EA6\u5F15\u5165 JMM\uFF0C\u7136\u540E\u4ECB\u7ECD JMM \u548C\u7269\u7406\u5185\u5B58\u4E4B\u95F4\u7684\u5173\u7CFB</p></blockquote><h2 id="jmm-\u5F15\u5165" tabindex="-1"><a class="header-anchor" href="#jmm-\u5F15\u5165" aria-hidden="true">#</a> JMM \u5F15\u5165</h2><p>\u5728\u5B66\u4E60 JMM \u4E4B\u524D\uFF0C\u5148\u7B80\u5355\u56DE\u987E\u4E00\u4E0B JVM \u8FD0\u884C\u65F6\u5185\u5B58\u7ED3\u6784</p><h3 id="jvm-\u5806\u6808\u5185\u5BB9" tabindex="-1"><a class="header-anchor" href="#jvm-\u5806\u6808\u5185\u5BB9" aria-hidden="true">#</a> JVM \u5806\u6808\u5185\u5BB9</h3><p><img src="https://wingbun-notes-image.oss-cn-guangzhou.aliyuncs.com/images/20220406100824.png" alt=""></p><ul><li><p>Heap \uFF08\u5806\uFF09\u5305\u542B\u4E86\u7A0B\u5E8F\u4E2D\u521B\u5EFA\u7684\u6240\u6709\u5BF9\u8C61\u3002\u65E0\u8BBA\u662F\u521B\u5EFA\u5BF9\u8C61\u5E76\u5C06\u5176\u5206\u914D\u7ED9\u5C40\u90E8\u53D8\u91CF\uFF0C\u8FD8\u662F\u521B\u5EFA\u4E3A\u53E6\u4E00\u4E2A\u5BF9\u8C61\u7684\u6210\u5458\u53D8\u91CF\uFF0C\u8BE5\u5BF9\u8C61\u4ECD\u7136\u5B58\u50A8\u5728\u5806\u4E0A\uFF0C\u5176\u6B21\u5BF9\u8C61\u4E0A\u7684\u6210\u5458\u53D8\u91CF\uFF0C\u65E0\u8BBA\u662F\u57FA\u672C\u7C7B\u578B\u8FD8\u662F\u5F15\u7528\u7C7B\u578B\u4E5F\u662F\u5B58\u50A8\u5728\u5806\u4E0A\u3002\u9759\u6001\u7C7B\u53D8\u91CF\u4E5F\u4E0E\u7C7B\u5B9A\u4E49\u4E00\u8D77\u5B58\u50A8\u5728\u5806\u4E0A\u3002</p></li><li><p>Thread Stack\uFF08\u7EBF\u7A0B\u6808\uFF09\u5305\u542B\u4E86\u6BCF\u4E2A\u65B9\u6CD5\u7684\u6240\u6709\u5C40\u90E8\u53D8\u91CF\u3002\u57FA\u672C\u7C7B\u578B\u7684\u5C40\u90E8\u53D8\u91CF\u5B8C\u5168\u5B58\u50A8\u5728\u7EBF\u7A0B\u6808\u4E2D\uFF0C\u4E00\u4E2A\u7EBF\u7A0B\u53EF\u4EE5\u5C06\u4E00\u4E2A\u57FA\u672C\u7C7B\u578B\u53D8\u91CF\u7684<strong>\u526F\u672C\u4F20\u9012</strong>\u7ED9\u53E6\u4E00\u4E2A\u7EBF\u7A0B\uFF0C\u4F46\u5B83\u4E0D\u80FD\u5171\u4EAB\u539F\u59CB\u5C40\u90E8\u53D8\u91CF\u672C\u8EAB\u3002\u5F15\u7528\u7C7B\u578B\u7684\u5C40\u90E8\u53D8\u91CF\uFF0C\u5F15\u7528\u5B58\u50A8\u5728\u7EBF\u7A0B\u6808\u4E2D\uFF0C\u6307\u5411\u7684\u5BF9\u8C61\u5B58\u50A8\u5728\u5806\u4E0A\u3002</p></li></ul><h3 id="java-\u5185\u5B58\u6A21\u578B" tabindex="-1"><a class="header-anchor" href="#java-\u5185\u5B58\u6A21\u578B" aria-hidden="true">#</a> Java \u5185\u5B58\u6A21\u578B</h3><p>\u5728\u5B66\u4E60\u4E86 JVM \u8FD0\u884C\u65F6\u5185\u5B58\u7ED3\u6784\u540E\uFF0C\u4E3A\u4E86\u8FDB\u4E00\u6B65\u5B66\u4E60 JVM \u600E\u4E48\u548C\u8BA1\u7B97\u673A\u786C\u4EF6\u5185\u5B58\u8FDB\u884C\u4EA4\u4E92\u7684\uFF0C\u9700\u8981\u518D\u5B66\u4E60 JMM(Java Memory Modal\uFF0CJava \u5185\u5B58\u6A21\u578B)</p><p>JVM \u4E3A\u4E86\u5C4F\u853D\u5404\u4E2A\u786C\u4EF6\u5E73\u53F0\u548C\u64CD\u4F5C\u7CFB\u7EDF\u5BF9\u5185\u5B58\u8BBF\u95EE\u673A\u5236\u7684\u5DEE\u5F02\uFF0C\u63D0\u51FA\u4E86 JMM \u7684\u6982\u5FF5</p><p><img src="https://wingbun-notes-image.oss-cn-guangzhou.aliyuncs.com/images/20220411162526.png" alt=""></p><p>\u73B0\u4EE3\u8BA1\u7B97\u673A\u786C\u4EF6\u5185\u5B58\u7ED3\u6784\u5927\u81F4\u53EF\u4EE5\u5206\u4E3A\u8FD9\u51E0\u4E2A\u533A\u57DF</p><ul><li>CPU\uFF1A\u73B0\u4EE3\u8BA1\u7B97\u673A\u901A\u5E38\u6709 2 \u4E2A\u6216\u66F4\u591A\u7684 CPU\uFF08\u5176\u4E2D\u4E00\u4E9B CPU \u6709\u53EF\u80FD\u6709\u591A\u4E2A\u5185\u6838\uFF09\uFF0C\u53EF\u4EE5\u540C\u65F6\u8FD0\u884C\u591A\u4E2A\u7EBF\u7A0B\uFF0C\u652F\u6301\u591A\u7EBF\u7A0B\u7A0B\u5E8F\u3002 <ul><li>CPU \u5BC4\u5B58\u5668\uFF1A\u6BCF\u4E2A CPU \u57FA\u672C\u4E0A\u5305\u542B\u4E00\u7EC4\u5728 CPU \u5185\u5B58\u4E2D\u7684\u5BC4\u5B58\u5668\uFF0CCPU \u5728\u8FD9\u4E9B\u5BC4\u5B58\u5668\u4E0A\u7684\u64CD\u4F5C\u6BD4\u5728\u4E3B\u5B58\u50A8\u5668\u7684\u64CD\u4F5C\u8981\u66F4\u5FEB\u3002</li></ul></li><li>CPU \u9AD8\u901F\u7F13\u5B58\u5B58\u50A8\u5668\uFF1ACPU \u53EF\u4EE5\u6BD4\u4E3B\u5B58\u50A8\u5668\u66F4\u5FEB\u5730\u8BBF\u95EE\u5176\u544A\u8BC9\u7F13\u5B58\u5B58\u50A8\u5668</li><li>\u4E3B\u5B58\u50A8\u5668\uFF1A\u4E3B\u8981\u5B58\u50A8\u7684\u662F Java \u5B9E\u4F8B\u5BF9\u8C61\uFF0C\u6240\u6709\u7EBF\u7A0B\u521B\u5EFA\u7684\u5B9E\u4F8B\u5BF9\u8C61\u90FD\u5B58\u653E\u5728\u4E3B\u5185\u5B58\u4E2D</li></ul><p>JMM \u89C4\u5B9A\u4E86\u4E3B\u5B58\u50A8\u5668\u6240\u6709\u7EBF\u7A0B\u90FD\u53EF\u4EE5\u8BBF\u95EE\uFF0C\u4F46\u662F\u7EBF\u7A0B\u5BF9\u53D8\u91CF\u7684\u64CD\u4F5C\u5FC5\u987B\u5728\u5176\u5DE5\u4F5C\u5185\u5B58\uFF08\u7EBF\u7A0B\u6808\uFF09\u4E2D\u8FDB\u884C\uFF0C\u64CD\u4F5C\u5B8C\u6210\u540E\u518D\u5199\u56DE\u4E3B\u5B58\u50A8\u5668\u4E2D\uFF0C\u4E0D\u80FD\u76F4\u63A5\u64CD\u4F5C\u4E3B\u5B58\u50A8\u5668\u4E2D\u7684\u53D8\u91CF\uFF0C\u5DE5\u4F5C\u5185\u5B58\u4E2D\u5B58\u50A8\u7740\u4E3B\u5B58\u50A8\u5668\u7684\u53D8\u91CF\u526F\u672C</p><h2 id="jmm-\u4E0E\u786C\u4EF6\u5185\u5B58\u7ED3\u6784\u5173\u7CFB" tabindex="-1"><a class="header-anchor" href="#jmm-\u4E0E\u786C\u4EF6\u5185\u5B58\u7ED3\u6784\u5173\u7CFB" aria-hidden="true">#</a> JMM \u4E0E\u786C\u4EF6\u5185\u5B58\u7ED3\u6784\u5173\u7CFB</h2><h3 id="jmm-\u4E0E\u786C\u4EF6\u5185\u5B58\u8FDE\u63A5" tabindex="-1"><a class="header-anchor" href="#jmm-\u4E0E\u786C\u4EF6\u5185\u5B58\u8FDE\u63A5" aria-hidden="true">#</a> JMM \u4E0E\u786C\u4EF6\u5185\u5B58\u8FDE\u63A5</h3><p><img src="https://wingbun-notes-image.oss-cn-guangzhou.aliyuncs.com/images/20220413162651.png" alt=""></p><p>\u786C\u4EF6\u67B6\u6784\u662F\u4E0D\u533A\u5206\u7EBF\u7A0B\u6808\u548C\u5806\u7684\uFF0C\u7EBF\u7A0B\u6808\u548C\u5806\u90FD\u4F4D\u4E8E\u4E3B\u5B58\u50A8\u5668\u4E2D\uFF0C\u6709\u4E00\u90E8\u5206\u53EF\u80FD\u4F1A\u4F4D\u4E8E CPU \u9AD8\u901F\u7F13\u5B58\u548C CPU \u5185\u90E8\u5BC4\u5B58\u5668\u4E2D</p><p>\u5F53\u5BF9\u8C61\u548C\u53D8\u91CF\u53EF\u4EE5\u5B58\u50A8\u5728\u4E0D\u540C\u7684\u5B58\u50A8\u533A\u57DF\u4E2D\uFF0C\u53EF\u80FD\u4F1A\u5F15\u53D1\u67D0\u4E9B\u95EE\u9898\uFF1A</p><ul><li>\u7EBF\u7A0B\u66F4\u65B0\uFF08\u5199\uFF09\u5171\u4EAB\u53D8\u91CF\u65F6\u7684\u53EF\u89C1\u6027</li><li>\u68C0\u67E5\u548C\u8BFB\u5199\u5171\u4EAB\u53D8\u91CF\u65F6\u7684\u7ADE\u6001\u6761\u4EF6</li></ul><p>\u6240\u4EE5 JMM \u89C4\u8303\u4E86 JVM \u4E0E\u8BA1\u7B97\u673A\u5185\u5B58\u662F\u5982\u4F55\u534F\u540C\u5DE5\u4F5C\u7684\uFF1A</p><ul><li>\u89C4\u5B9A\u4E86\u4E00\u4E2A\u7EBF\u7A0B\u5982\u4F55\u548C\u4F55\u65F6\u53EF\u4EE5\u770B\u5230\u7531\u5176\u4ED6\u7EBF\u7A0B\u4FEE\u6539\u8FC7\u540E\u7684\u5171\u4EAB\u53D8\u91CF\u7684\u503C</li><li>\u89C4\u5B9A\u4E86\u4E00\u4E2A\u7EBF\u7A0B\u5728\u5FC5\u987B\u65F6\u5982\u4F55\u540C\u6B65\u5730\u8BBF\u95EE\u5171\u4EAB\u53D8\u91CF</li></ul><p>\u901A\u8FC7\u8FD9\u79CD\u65B9\u5F0F\u6765\u4FDD\u8BC1\u591A\u7EBF\u7A0B\u4E0B\u53D8\u91CF\u7684\u7F13\u5B58\u4E00\u81F4\u6027\u95EE\u9898</p><h3 id="\u5BF9\u8C61\u5171\u4EAB\u540E\u7684\u53EF\u89C1\u6027" tabindex="-1"><a class="header-anchor" href="#\u5BF9\u8C61\u5171\u4EAB\u540E\u7684\u53EF\u89C1\u6027" aria-hidden="true">#</a> \u5BF9\u8C61\u5171\u4EAB\u540E\u7684\u53EF\u89C1\u6027</h3><blockquote><p>\u5982\u679C\u591A\u4E2A\u7EBF\u7A0B\u5171\u4EAB\u4E00\u4E2A\u5BF9\u8C61\uFF0C\u90A3\u4E48\u5176\u4E2D\u4E00\u4E2A\u7EBF\u7A0B\u5BF9\u8FD9\u4E2A\u5BF9\u8C61\u7684\u66F4\u65B0\u53EF\u80FD\u5BF9\u5176\u4ED6\u7EBF\u7A0B\u4E0D\u53EF\u89C1\u3002</p></blockquote><p>\u5171\u4EAB\u5BF9\u8C61\u6700\u521D\u662F\u653E\u5728\u4E86\u4E3B\u5B58\u50A8\u5668\u4E2D\uFF0C\u7136\u540E\u5728 CPU \u4E0A\u8FD0\u884C\u7684\u7EBF\u7A0B\u628A\u5171\u4EAB\u5BF9\u8C61\u8BFB\u5165\u5230\u5176 CPU \u9AD8\u901F\u7F13\u5B58\u4E2D\uFF0C\u7136\u540E\u5BF9\u5176\u8FDB\u884C\u4E86\u4FEE\u6539\u3002\u53EA\u8981 CPU \u9AD8\u901F\u7F13\u5B58\u8FD8\u6CA1\u66F4\u65B0\u5230\u4E3B\u5B58\u50A8\u5668\u4E2D\uFF0C\u8FD9\u4E2A\u5BF9\u8C61\u7684\u6539\u52A8\u5BF9\u5176\u4ED6\u7EBF\u7A0B\u662F\u4E0D\u53EF\u89C1\u7684\uFF0C\u6BCF\u4E00\u4E2A\u7EBF\u7A0B\u90FD\u62E5\u6709\u4E86\u8FD9\u4E2A\u5171\u4EAB\u5BF9\u8C61\u526F\u672C\uFF0C\u6BCF\u4E2A\u526F\u672C\u90FD\u4F4D\u4E8E\u4E0D\u540C\u7684 CPU \u9AD8\u901F\u7F13\u5B58\u4E2D</p><p><img src="https://wingbun-notes-image.oss-cn-guangzhou.aliyuncs.com/images/20220413163651.png" alt=""></p><blockquote><p>\u4E0A\u56FE\u4E2D\uFF0C\u7B2C\u4E00\u4E2A\u7EBF\u7A0B\u5BF9\u5171\u4EAB\u5BF9\u8C61 obj \u7684 count \u5C5E\u6027\u4FEE\u6539\u4E3A 2\uFF0C\u4F46\u5176 CPU \u9AD8\u901F\u7F13\u5B58\u5E76\u672A\u5237\u5230\u4E3B\u5B58\u50A8\u5668\u4E2D\uFF0C\u5BFC\u81F4\u53E6\u4E00\u4E2A\u7EBF\u7A0B\u65E0\u6CD5\u611F\u77E5\u8FD9\u4E2A\u6539\u52A8</p></blockquote><p>\u89E3\u51B3\u65B9\u6848\uFF1A<strong>\u4F7F\u7528 <code>volatile</code> \u5173\u952E\u5B57\u6765\u58F0\u660E\u8FD9\u4E2A\u5BF9\u8C61</strong>\u3002\u8FD9\u4E2A\u5173\u952E\u5B57\u53EF\u4EE5\u4FDD\u8BC1\u76F4\u63A5\u4ECE\u4E3B\u5185\u5B58\u5668\u8BFB\u53D6\u7ED9\u5B9A\u53D8\u91CF\uFF0C<strong>\u5E76\u5728\u66F4\u65B0\u65F6\u59CB\u7EC8\u5199\u56DE\u4E3B\u5185\u5B58</strong></p><h3 id="\u7ADE\u6001\u6761\u4EF6" tabindex="-1"><a class="header-anchor" href="#\u7ADE\u6001\u6761\u4EF6" aria-hidden="true">#</a> \u7ADE\u6001\u6761\u4EF6</h3><blockquote><p>\u5982\u679C\u591A\u4E2A\u7EBF\u7A0B\u5171\u4EAB\u4E00\u4E2A\u5BF9\u8C61\uFF0C\u5E76\u4E14\u591A\u4E2A\u7EBF\u7A0B\u66F4\u65B0\u8BE5\u5171\u4EAB\u5BF9\u8C61\u4E2D\u7684\u53D8\u91CF\uFF0C\u53EF\u80FD\u4F1A\u51FA\u73B0\u7ADE\u6001</p></blockquote><p><img src="https://wingbun-notes-image.oss-cn-guangzhou.aliyuncs.com/images/20220413165751.png" alt=""></p><blockquote><p>\u4E0A\u56FE\u4E2D\uFF0C\u5047\u8BBE A \u7EBF\u7A0B\u628A\u5171\u4EAB\u5BF9\u8C61 obj \u8BFB\u5165\u81EA\u5DF1\u7684 CPU \u9AD8\u901F\u7F13\u5B58\u4E2D\u5E76\u5BF9 obj \u7684 count \u5C5E\u6027 +1\uFF0C\u5E76\u4E14 B \u7EBF\u7A0B\u4E5F\u5728\u505A\u540C\u6837\u7684\u4E8B\u60C5</p><p>\u5982\u679C\u8FD9\u4E2A\u52A0\u6CD5\u64CD\u4F5C\u6709\u5E8F\u8FDB\u884C\u4E86\uFF0C\u90A3\u4E48\u8FD9\u4E2A\u5C5E\u6027\u6700\u7EC8\u5199\u5165\u5230\u4E3B\u5B58\u50A8\u5668\u4E2D\u5E94\u8BE5\u662F\u8981 +2 \u7684</p><p>\u4F46\u662F\u8FD9\u4E24\u4E2A\u52A0\u6CD5\u64CD\u4F5C\u6CA1\u6709\u505A\u5230\u540C\u6B65\u7684\u63A7\u5236\uFF0C\u65E0\u8BBA\u54EA\u4E00\u4E2A\u7EBF\u7A0B\u628A\u81EA\u5DF1 CPU \u9AD8\u901F\u7F13\u5B58\u4E2D\u7684\u5BF9\u8C61\u5199\u5165\u5230\u4E3B\u5B58\u50A8\u5668\u4E2D\u65F6\uFF0C\u6700\u7EC8\u8FD9\u4E2A\u5C5E\u6027\u4E5F\u53EA\u662F\u76F8\u8F83\u539F\u6765 +1</p></blockquote><p>\u89E3\u51B3\u65B9\u6848\uFF1A<strong>\u4F7F\u7528 <code>synchronized</code> \u5173\u952E\u5B57\u5305\u542B\u8FD9\u4E2A\u52A0\u6CD5\u64CD\u4F5C</strong>\u3002\u88AB <code>synchronized</code> \u5305\u4F4F\u7684\u4EE3\u7801\u5757\u53EF\u4EE5\u4FDD\u8BC1\u5728\u540C\u4E00\u65F6\u95F4\u53EA\u80FD\u6709\u4E00\u4E2A\u7EBF\u7A0B\u8FDB\u5165\u4EE3\u7801\u5757\u4E2D\uFF0C<strong>\u4FDD\u8BC1\u4E86\u4EE3\u7801\u5757\u4E2D\u7684\u53D8\u91CF\u90FD\u4ECE\u4E3B\u5B58\u4E2D\u8BFB\u53D6</strong>\uFF0C\u5E76\u4E14\u4E0D\u7BA1\u53D8\u91CF\u662F\u5426\u88AB <code>volatile</code> \u4FEE\u9970\uFF0C<strong>\u6240\u6709\u66F4\u65B0\u90FD\u59CB\u7EC8\u5199\u5165\u5230\u4E3B\u5B58\u50A8\u5668\u4E2D</strong></p>',34),s=[t];function r(c,l){return e(),o("div",null,s)}var d=a(n,[["render",r],["__file","\u7269\u7406\u5185\u5B58\u6A21\u578B.html.vue"]]);export{d as default};
