const e=JSON.parse('{"key":"v-1fe7f7a6","path":"/writings/JVM/%E7%BA%BF%E7%A8%8B%E5%A0%86%E6%A0%88%E5%88%86%E6%9E%90.html","title":"线程堆栈分析","lang":"zh-CN","frontmatter":{"description":"线程堆栈分析 Thread Dump是非常有用的诊断Java应用问题的工具 介绍 Thread Dump 是非常有用的诊断 Java 应用问题的工具。每一个 JVM 都有及时生成所有线程在某一点状态的thread-dump的能力。它提供了当前活动线程的快照，及JVM中所有Java线程的堆栈跟踪信息，堆栈信息一般包含完整的类名及所执行的方法，甚至还有源代码的行数，并且执行的时候不影响系统的性能。 抓取 一般系统挂起、崩溃或者性能低下时，需要抓取系统的线程堆栈（Thread Dump）来进行后续的分析。但是由于Thread Dump只是一个线程的快照，往往不足以确定问题。一般每次会间隔20-30秒，至少抓取3次dump信息，如果多次dump信息都指向同一个问题，那么问题可能就被确定了。","head":[["meta",{"property":"og:url","content":"https://gelald.github.io/javrin/javrin/writings/JVM/%E7%BA%BF%E7%A8%8B%E5%A0%86%E6%A0%88%E5%88%86%E6%9E%90.html"}],["meta",{"property":"og:site_name","content":"Javrin"}],["meta",{"property":"og:title","content":"线程堆栈分析"}],["meta",{"property":"og:description","content":"线程堆栈分析 Thread Dump是非常有用的诊断Java应用问题的工具 介绍 Thread Dump 是非常有用的诊断 Java 应用问题的工具。每一个 JVM 都有及时生成所有线程在某一点状态的thread-dump的能力。它提供了当前活动线程的快照，及JVM中所有Java线程的堆栈跟踪信息，堆栈信息一般包含完整的类名及所执行的方法，甚至还有源代码的行数，并且执行的时候不影响系统的性能。 抓取 一般系统挂起、崩溃或者性能低下时，需要抓取系统的线程堆栈（Thread Dump）来进行后续的分析。但是由于Thread Dump只是一个线程的快照，往往不足以确定问题。一般每次会间隔20-30秒，至少抓取3次dump信息，如果多次dump信息都指向同一个问题，那么问题可能就被确定了。"}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"zh-CN"}],["meta",{"property":"og:updated_time","content":"2023-02-28T15:20:13.000Z"}],["meta",{"property":"article:author","content":"gelald"}],["meta",{"property":"article:modified_time","content":"2023-02-28T15:20:13.000Z"}],["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"线程堆栈分析\\",\\"image\\":[\\"\\"],\\"dateModified\\":\\"2023-02-28T15:20:13.000Z\\",\\"author\\":[{\\"@type\\":\\"Person\\",\\"name\\":\\"gelald\\",\\"url\\":\\"https://github.com/gelald\\"}]}"]]},"headers":[{"level":2,"title":"介绍","slug":"介绍","link":"#介绍","children":[]},{"level":2,"title":"抓取","slug":"抓取","link":"#抓取","children":[]},{"level":2,"title":"分析","slug":"分析","link":"#分析","children":[{"level":3,"title":"Dump 信息","slug":"dump-信息","link":"#dump-信息","children":[]},{"level":3,"title":"Java Thread Stack Trace 详解","slug":"java-thread-stack-trace-详解","link":"#java-thread-stack-trace-详解","children":[]},{"level":3,"title":"线程状态分析","slug":"线程状态分析","link":"#线程状态分析","children":[]}]}],"git":{"createdTime":1677597613000,"updatedTime":1677597613000,"contributors":[{"name":"gelald","email":"yb.ng@foxmail.com","commits":1}]},"readingTime":{"minutes":4.56,"words":1368},"filePathRelative":"writings/JVM/线程堆栈分析.md","localizedDate":"2023年2月28日","excerpt":"<h1> 线程堆栈分析</h1>\\n<blockquote>\\n<p>Thread Dump是非常有用的诊断Java应用问题的工具</p>\\n</blockquote>\\n<h2> 介绍</h2>\\n<p>Thread Dump 是非常有用的诊断 Java 应用问题的工具。每一个 JVM 都有及时生成所有线程在某一点状态的thread-dump的能力。它提供了当前活动线程的快照，及JVM中所有Java线程的堆栈跟踪信息，堆栈信息一般包含完整的类名及所执行的方法，甚至还有源代码的行数，并且执行的时候不影响系统的性能。</p>\\n<h2> 抓取</h2>\\n<p>一般系统挂起、崩溃或者性能低下时，需要抓取系统的线程堆栈（Thread Dump）来进行后续的分析。但是由于Thread Dump只是一个线程的快照，往往不足以确定问题。一般每次会间隔20-30秒，至少抓取3次dump信息，如果多次dump信息都指向同一个问题，那么问题可能就被确定了。</p>","copyright":{"author":"gelald","license":"MIT Licensed"},"autoDesc":true}');export{e as data};
